mapper.select:
SELECT DATE_FORMAT(collectionTime,"%Y-%m-%d") days,count(*) nums
  FROM floatingmatter
  WHERE
    DATE_FORMAT(collectionTime,'%Y-%m-%d') >= #{startTime}
    AND
    DATE_FORMAT(collectionTime,'%Y-%m-%d')  <=  #{endTime}
    AND
    stnId = #{stnId}
  GROUP BY days;

SELECT DATE_FORMAT(collectionTime,"%Y-%m-%d") recordTime,count(*) nums
FROM floatingmatter
WHERE
stnId = #{stnId}
GROUP BY recordTime
LIMIT #{days};

SELECT stnId,collectionTime,camIndex,filePathOrigin,filePathResult,picOrVideo
FROM floatingmatter
WHERE
DATE_FORMAT(collectionTime,'%Y-%m-%d') = #{date}
AND
stnId = #{stnId}

SELECT stnId,collectionTime,camIndex,filePathOrigin,filePathResult,picOrVideo
  FROM floatingmatter
  WHERE
    DATE_FORMAT(collectionTime,'%Y-%m-%d') >= #{startTime}
    AND
    DATE_FORMAT(collectionTime,'%Y-%m-%d')  <=  #{endTime}
    AND
    stnId = #{stnId}

SELECT  count(*)
FROM floatingmatter
WHERE stnId = #{stnId}
