mapper.select:
SELECT stnId,avgSpeed,riverArea,avgFlow,collectionTime,state,dayFlow,weekFlow ,monthFlow,yearFlow,totalFlow,remark
FROM flow
WHERE
  collectionTime =
  (
  SELECT MAX(collectionTime)
  FROM flow
  WHERE stnId = #{stnId}
  )
and stnId = #{stnId}

SELECT avgSpeed,riverArea,avgFlow,collectionTime,state,dayFlow,weekFlow,monthFlow,yearFlow,totalFlow,remark
FROM flow
WHERE
  stnId = #{stnId}
  and collectionTime >= #{startTime}
  and collectionTime  <=  #{endTime}
  ORDER by collectionTime

SELECT stnId,avgSpeed,riverArea,avgFlow,collectionTime,state,dayFlow,weekFlow ,monthFlow,yearFlow,totalFlow,remark
FROM flow
WHERE 1=1

      and stnId = #{stnId}

  and collectionTime >= #{startTime}
  and collectionTime  <=  #{endTime}

mapper.delete:
DELETE
  FROM flow
  WHERE flowId = #{flowId} and collectionTime=#{collectionTime}

mapper.select:
SELECT *
FROM flow
WHERE
stnId = #{stnId}
ORDER by collectionTime DESC
limit #{days}

SELECT flow.avgFlow,water.waterLevel,flow.collectionTime,speedflow.FilePath1,
speedflow.FilePath2,speedflow.FilePath3,speedflow.FilePath4,speedflow.FilePath5
FROM water,flow,speedflow
WHERE
  flow.collectionTime =
  (
  SELECT MAX(collectionTime)
  FROM flow
  WHERE flow.collectionTime  <=  #{time}
  AND flow.stnId = #{stnId}
  )
and speedflow.stnId  = #{stnId}
and water.c_time =
(
SELECT MAX(c_time)
FROM water
WHERE c_time  <=  #{time}
AND stnId = #{stnId}
)
and flow.collectionTime = speedflow.collectionTime

SELECT  count(*)
FROM flow
WHERE stnId = #{stnId}
